from io import StringIO
import tensorflow as tf 
gpus = tf.config.experimental.list_physical_devices(device_type='GPU')
for gpu in gpus:
    tf.config.experimental.set_memory_growth(gpu, True)


sys.stdout.reconfigure(encoding = 'utf-8')

uri=""
print("模型載入開始!!!")
model = keras.models.load_model('../model/DenseNet201_for_15_category_origin')
def select_disease(disease_number):
    mydb = mysql.connector.connect(
        host="localhost",
        user="root",
        passwd="",
        database="hos"
        )
    mycursor = mydb.cursor()
    sql = f'select * from disease where id = {disease_number}'
    mycursor.execute(sql)
    myresult = mycursor.fetchall()
    disease = {
        "name" : myresult[0][0],
        "symptom" : myresult[0][3], 
        "urgency" : myresult[0][4]
    }
 
    mydb.close()
    # jsonOutput = json.dump(disease, ensure_ascii = False)
    # return jsonOutput
    return disease

while (uri==""):
    f =open("uri.txt","r+",encoding="utf-8")
    uri = f.read()
    # uri = "data:image/jpeg;base64,…"
    f =open("uri.txt","w+",encoding="utf-8")
    f.close()
    if (uri !=""):
        uri = parse_data_uri(uri)[2]
        mg_data = plt.imread(BytesIO(uri),"jpg")
        mg_data = cv2.resize(mg_data, (224, 224))
        mg_data = (mg_data / 255.0).reshape((-1, 224, 224, 3))
        num = list(model.predict(mg_data))
        num = model.predict(mg_data).argmax()
        number = num + 1

        # number = model.predict(mg_data).argmax()

        print(num)
        print(number)
        # number=1
        print("確認資料，讀寫中......")
        sickans = select_disease(number)

        with open('sickans.json', 'w', encoding='utf-8') as f:
            json.dump(sickans,f)
            f.close()
        print(sickans)
        print("資料讀寫完畢......")
        uri = ""
        # break
    print("等待資料中.....")
    time.sleep(10)











